//------------------------------------------------------------------------------
//Callback Name: filter_cb
//------------------------------------------------------------------------------
int SpraySequencerandCSVOutputDialog::filter_cb(NXOpen::BlockStyler::UIBlock* block, NXOpen::TaggedObject* selectObject)
{
    // 对曲线选择器应用 IsSprayPath 属性过滤
    if (block == all_curves_sel || block == start_curve_sel || block == csv_curve_select) {
        try {
            NXOpen::NXObject* nxObj = dynamic_cast<NXOpen::NXObject*>(selectObject);
            if (nxObj != nullptr) {
                // 检查曲线是否有 IsSprayPath 属性且值为 1
                int isSprayPath = nxObj->GetIntegerAttribute("IsSprayPath");
                if (isSprayPath == 1) {
                    return UF_UI_SEL_ACCEPT;
                }
            }
        }
        catch (...) {
            // 属性不存在或读取失败，拒绝选择
        }
        return UF_UI_SEL_REJECT;
    }

    return(UF_UI_SEL_ACCEPT);
}
