//------------------------------------------------------------------------------
// 设置起点约束
//------------------------------------------------------------------------------
void SpraySequencerandCSVOutputDialog::SetStartConstraints()
{
    // 获取起点位置
    Point3d startPoint = startpoint_sel->Point();

    // 获取起点曲线
    PropertyList* curveProps = start_curve_sel->GetProperties();
    std::vector<TaggedObject*> startCurves =
        curveProps->GetTaggedObjectVector("SelectedObjects");
    delete curveProps;

    // 设置约束
    if (!startCurves.empty()) {
        // 通过点找最近端点（自动匹配曲线）
        m_pathSequencer.setStartByPickedPoint(startPoint);
    }
    else {
        // 如果未选择起点曲线，清除约束
        m_pathSequencer.clearStartConstraint();
    }
}

//------------------------------------------------------------------------------
// 填充优化序列到列表
//------------------------------------------------------------------------------
void SpraySequencerandCSVOutputDialog::PopulateCurveSequenceList()
{
    // 获取优化后的序列
    std::vector<std::pair<int, bool>> sequence =
        m_pathSequencer.getFinalSequence();

    // 构建列表项字符串
    std::vector<NXString> itemStrings;
    for (size_t i = 0; i < sequence.size(); i++) {
        int pathIdx = sequence[i].first;

        //  bool 含义是 entryIsStart (true=正向, false=反向)
        bool entryIsStart = sequence[i].second;

        // 格式: "序号  1: 路径  5 [正向]"
        std::stringstream ss;
        ss << "序号 " << std::setw(3) << (i + 1)
            << ": 路径 " << std::setw(3) << pathIdx
            << (entryIsStart ? " [正向]" : " [反向]"); // <--- 修改这里：判断逻辑反转
        // --- 修改结束 ---

        itemStrings.push_back(ss.str());
    }

    // 设置列表项
    PropertyList* listProps = curve_sequence_list->GetProperties();
    listProps->SetStrings("ListItems", itemStrings);
    delete listProps;
}

//------------------------------------------------------------------------------
// 执行优化并显示结果
//------------------------------------------------------------------------------
void SpraySequencerandCSVOutputDialog::OptimizeAndDisplayPaths()
{
    // Step 1: 清空之前的数据
    m_pathSequencer.clearPaths();

    // Step 2: 获取选中的喷涂曲线
    PropertyList* allCurvesProps = all_curves_sel->GetProperties();
    std::vector<TaggedObject*> selectedCurves =
        allCurvesProps->GetTaggedObjectVector("SelectedObjects");
    delete allCurvesProps;

    // Step 3: 验证曲线数量
    if (selectedCurves.empty()) {
        ShowErrorMessage("请选择喷涂路径曲线！");
        return;
    }

    if (selectedCurves.size() < 3) {
        ShowErrorMessage("请至少选择3条曲线才能启动遗传算法优化！");
        return;
    }

    // Step 4: 添加路径到优化器
    m_pathSequencer.addPathsFromUI(selectedCurves);

    // Step 5: 设置起点约束
    SetStartConstraints();

    // Step 6: 执行遗传算法优化
    m_pathSequencer.solve();

    // Step 7: 填充结果到列表
    PopulateCurveSequenceList();

    // Step 8: 自动填充优化后的曲线到 CSV 选择器
    std::vector<NXOpen::TaggedObject*> optimizedCurves = m_pathSequencer.getOptimizedCurves();
    if (!optimizedCurves.empty()) {
        PropertyList* csvSelectProps = csv_curve_select->GetProperties();
        csvSelectProps->SetTaggedObjectVector("SelectedObjects", optimizedCurves);
        delete csvSelectProps;
    }

    // Step 9: 创建可视化连接线（Layer 89）
   m_pathSequencer.createConnectionLines();

    // Step 10: 生成详细日志文件
    m_pathSequencer.printResult();

    // Step 11: 弹窗提示优化完成
    double distance = m_pathSequencer.getBestDistance();
    std::stringstream msg;
    msg << "遗传算法优化完成！\n\n"
        << "曲线数量: " << selectedCurves.size() << "\n"
        << "总空移距离: " << std::fixed << std::setprecision(2)
        << distance << " mm\n\n"
        << "优化序列已显示在列表中\n";

    ShowInfoMessage(msg.str().c_str());
}



//------------------------------------------------------------------------------
// 主CSV导出函数 - UI回调
//------------------------------------------------------------------------------
void SpraySequencerandCSVOutputDialog::ExportCSVFiles()
{
    try {
        // 从 UI 获取选中曲线
        std::vector<TaggedObject*> selectedCurves = csv_curve_select->GetSelectedObjects();

        // 验证曲线数量
        if (selectedCurves.empty()) {
            ShowErrorMessage("请先选择要导出的喷涂曲线！");
            return;
        }

        // 从 UI 获取喷涂角度
        PropertyList* angleProps = sprayangle_double->GetProperties();
        double sprayAngle = angleProps->GetDouble("Value");
        delete angleProps;

        // 调用 CSVExporter 静态工具类执行导出
        CSVExporter::ExportCSVFiles(
            selectedCurves,      // 选中的曲线列表
            sprayAngle,          // 喷涂角度
            m_pathSequencer,     // 路径排序器（获取方向信息）
            "C:\\txtfile"  // 输出目录
        );

        // 显示成功消息
        ShowInfoMessage("CSV导出成功！\n\n请查看输出目录：\nC:\\txtfile");
    }
    catch (std::exception& ex) {
        std::string errorMsg = "CSV导出过程中发生错误：\n";
        errorMsg += ex.what();
        ShowErrorMessage(errorMsg.c_str());
    }
}
